mapper.select:
SELECT USER_ID
FROM FREE_USER 
WHERE USER_ID = #{USER_ID}

SELECT USER_EMAIL
FROM FREE_USER 
WHERE USER_EMAIL = #{USER_EMAIL}

SELECT USER_ID
FROM FREE_USER 
WHERE USER_NAME = #{USER_NAME} AND USER_EMAIL = #{USER_EMAIL}

SELECT USER_ID
FROM FREE_USER 
WHERE USER_ID = #{USER_ID} AND USER_NAME = #{USER_NAME} AND USER_EMAIL = #{USER_EMAIL}

SELECT USER_ID
FROM FREE_USER 
WHERE USER_ID = #{USER_ID} AND USER_PW = #{USER_PW}

mapper.update:
		UPDATE FREE_USER
    	SET USER_PW = #{USER_PW}
    	WHERE USER_ID = #{USER_ID}

mapper.select:
SELECT USER_NAME 
FROM FREE_USER 
WHERE USER_ID = #{USER_ID} AND USER_PW = #{USER_PW}

mapper.update:
		UPDATE FREE_USER
    	SET FREELANCER_IMAGE_PATH = #{FREELANCER_IMAGE_PATH}
    	WHERE USER_ID = #{USER_ID}

mapper.select:
	SELECT * 
	FROM (
		SELECT ROWNUM RN, A.* 
			FROM (
					SELECT * 
					FROM FREE_USER
					WHERE freelancer_public = 1
					) A
			)
WHERE RN BETWEEN #{start} AND #{end}
